<?php 
$date = date("d-m-Y");

$jour= date("l");
$heure = date("H:i");

echo "<br>";
if($jour=="Monday"){
  Print("Nous sommes le Lundi $date et il est $heure");
}elseif ($jour=="Tuesday") {
  Print("Nous sommes le Mardi $date et il est $heure");
}elseif ($jour=="Wednesday") {
  Print("Nous sommes le Mercedi $date et il est $heure");
}elseif ($jour=="Thursday") {
  Print("Nous sommes le Jeudi $date et il est $heure");
}elseif ($jour=="Friday") {
  Print("Nous sommes le Vendredi $date et il est $heure");
}elseif ($jour=="Saturday") {
  Print("Nous sommes le Samedi $date et il est $heure");
}elseif ($jour=="Sunday") {
  Print("Nous sommes le Dimanche $date et il est $heure");
}
 ?>
